// PlaceOrder.js

import React, { useState } from "react";
import {
  Box,
  Text,
  Button,
  VStack,
  HStack,
  Input,
  InputGroup,
  InputLeftAddon,
  Image,
  Divider,
} from "@chakra-ui/react";

export const PlaceOrder = () => {
  const [items, setItems] = useState([
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febd",
      quantity: 2,
    },
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febes",
      quantity: 3,
    },
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febes",
      quantity: 3,
    },
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febes",
      quantity: 3,
    },
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febes",
      quantity: 3,
    },
    {
      image:
        "data:image/jpeg;base64,",
      name: "school bag",
      price: 1100,
      stock: 90,
      _id: "6652239da61f4e271fc9febes",
      quantity: 3,
    },
  ]);

  const calculateTotal = () => {
    return items.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    setItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId ? { ...item, quantity: newQuantity } : item,
      ),
    );
  };

  return (
    <Box
      p={4}
      width="90%"
      display="flex"
      flexDirection="column"
      justifyContent="space-between"
    >
      <Box  paddingTop='60px'  paddingBottom="67px">
        <VStack spacing={4} align="stretch">
          {items.map((item) => (
            <HStack key={item.id} justifyContent="space-between">
              <Image src={item.image} borderRadius="full" boxSize="150px" />
              <Text>{item.name}</Text>
              <InputGroup size="sm" width="80px">
                <InputLeftAddon children="Qty" />
                <Input
                  type="number"
                  value={item.quantity}
                  onChange={(e) =>
                    handleQuantityChange(item.id, parseInt(e.target.value, 10))
                  }
                />
              </InputGroup>
              <Text>${(item.price * item.quantity).toFixed(2)}</Text>
            </HStack>
          ))}
        </VStack>
      </Box>
      <Box
        position="fixed"
        bottom={0}
        left={0}
        right={0}
        width="100%"
        background="#87959c"
        p={4}
        display="flex"
        zIndex="10"
      >
        <Box
          size="lg"
          borderRadius="10px"
          w="50%"
          display="flex"
          justifyContent="center"
          alignItems="center"
        >
          <Text fontSize="2xl" fontWeight="bold">
            Total: ${calculateTotal().toFixed(2)}
          </Text>
        </Box>
        <Button colorScheme="teal" size="lg" p={4} w="50%" border="solid">
          Place Order
        </Button>
      </Box>
    </Box>
  );
};

export default PlaceOrder;
